import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components'


function LoginForm() {
    let navigate = useNavigate(); 
    const routeChange = () =>{ 
      let path = `admin`; 
      navigate(path);
    }
 function handleClick(e){
    e.preventDefault();
    if(adminDetails.userName==username&&adminDetails.passWord==password){
        alert('Username is correct')
        alert('Password is correct')
        setUsername('')
        setPassword('')
        routeChange();
    }else{
        alert('Please check your credentials.')
    }
}   

const [username, setUsername] = useState('')
const [password, setPassword] = useState('')
  const handleUsername = event => {
    setUsername(event.target.value);
  };
  const handlePassword = event => {
    setPassword(event.target.value);
  }

  return (
    <StyledContainer>
        <form >
            <div>
                <h1>Sign in:</h1>
            </div>
            <div clasName="container">
                <label for="uname"><b>Username</b></label>
                <input type="text" placeholder="Enter Username" name="uname" required onChange={handleUsername} value={username}/><br/>
                <label for="psw"><b>Password </b></label>
                <input type="password" placeholder="Enter Password" name="psw" required onChange={handlePassword} value={password}/><br/>
                <button onClick={handleClick} type="submit">Login</button>
            </div>
        </form>
    </StyledContainer>
  )
}
const adminDetails = {
    userName:"admin",
    passWord:"admin123"
}
const StyledContainer = styled.div`
    margin:0 auto;
    width:100%;
    max-width:1480px;
    margin-top:50px;
    h1,label,input{
        color:#012c78;
    }
    input{
        margin-left:25px;
        margin-bottom:10px;
    }
    button{
        margin-top:50px;
        margin-left:205px;
        background-color:#012c78;
        color:white;
        padding:8px 16px;
        border-radius:10px;
    }
`

export default LoginForm
